CREATE DEFINER=`root`@`%` PROCEDURE `SPL_Proveedor`( IN TipoLista INT)
BEGIN
	DECLARE Message VARCHAR(50);

    -- Verificar el tipo de lista proporcionado y realizar la consulta correspondiente
    IF TipoLista = 1 THEN
        -- Clientes habilitados
        SELECT 
            p.IdPersona,
            p.IdTipoPersona,
            p.IdLocalidad,
            p.IdTipoDocumentacion,
            p.Documentacion, 
            p.Mail, 
            p.RazonSocial,
            p.FechaNacimiento,
            p.Telefono,
            p.FechaAlta, 
            p.FechaBaja,
            d.Calle,
            d.Nro,
            d.Piso,
            d.IdTipoDomicilio,
            l.IdProvincia
        FROM 
            Persona p
            LEFT JOIN Domicilio d ON p.IdDomicilio = d.IdDomicilio
            LEFT JOIN Localidad l ON p.IdLocalidad = l.IdLocalidad
        WHERE 
            p.FechaBaja IS NULL 
            AND p.IdTipoPersonaSistema = 2;
    ELSEIF TipoLista = 2 THEN
        -- Clientes inhabilitados
        SELECT 
            p.IdPersona,
            p.IdTipoPersona,
            p.IdLocalidad,
            p.IdTipoDocumentacion,
            p.Documentacion,
            p.Mail, 
            p.RazonSocial,
            p.FechaNacimiento,
            p.Telefono,
            p.FechaAlta, 
            p.FechaBaja,
            d.Calle,
            d.Nro,
            d.Piso,
            d.IdTipoDomicilio,
            l.IdProvincia
        FROM 
            Persona p
            LEFT JOIN Domicilio d ON p.IdDomicilio = d.IdDomicilio
            LEFT JOIN Localidad l ON p.IdLocalidad = l.IdLocalidad
        WHERE 
            p.FechaBaja IS NOT NULL 
            AND p.IdTipoPersonaSistema = 2;
    ELSE
        -- Tipo de lista no válido
        SET Message = 'Tipo de lista no válido';
        SELECT Message;
    END IF;
END ;;
